// 便利函数
// 获取所有变量名
function getAllVariableNames() {
    return Object.keys(codeFiles);
}

// 根据索引获取变量名
function getVariableNameByIndex(index) {
    const names = Object.keys(codeFiles);
    return names[index - 1] || null;
}

// 获取文件内容
function getFileContent(variableName) {
    const file = codeFiles[variableName];
    return file ? file.content : null;
}

// 获取文件信息
function getFileInfo(variableName) {
    return codeFiles[variableName] || null;
}

// 根据索引获取文件信息
function getFileInfoByIndex(index) {
    const varName = getVariableNameByIndex(index);
    return varName ? getFileInfo(varName) : null;
}

// 获取所有文件列表
function getAllFilesList() {
    return Object.keys(codeFiles).map(varName => ({
        variableName: varName,
        ...codeFiles[varName]
    }));
}

// 搜索文件
function searchFiles(keyword) {
    const results = [];
    for (const [varName, file] of Object.entries(codeFiles)) {
        if (file.filename.toLowerCase().includes(keyword.toLowerCase()) ||
            file.content.toLowerCase().includes(keyword.toLowerCase())) {
            results.push({ variableName: varName, ...file });
        }
    }
    return results;
}

// 反转义函数（用于显示）
function unescapeContent(content) {
    return content
        .replace(/\\/g, '\\')
        .replace(/\"/g, '"')
        .replace(/\\b/g, '\b')
        .replace(/\\f/g, '\f')
        .replace(/\\n/g, '\n')
        .replace(/\\r/g, '\r')
        .replace(/\\t/g, '\t');
}

// 获取反转义的文件内容
function getFileContentUnescaped(variableName) {
    const content = getFileContent(variableName);
    return content ? unescapeContent(content) : null;
}
